package uz.hamrohimport android.os.Bundleimport android.view.LayoutInflaterimport androidx.activity.compose.setContentimport androidx.compose.material.Surfaceimport androidx.compose.runtime.Composableimport androidx.compose.ui.viewinterop.AndroidViewimport com.github.terrakok.cicerone.Navigatorimport com.github.terrakok.cicerone.NavigatorHolderimport dagger.hilt.android.AndroidEntryPointimport uz.hamroh.feature.authorization.AuthScreensimport uz.hamroh.feature.authorization.ui.auth_selection.AuthSelectionFragmentimport uz.hamroh.navigation.AppCoordinatorimport uz.hamroh.ui.base.BaseActivityimport uz.hamroh.ui.base.CustomAppNavigatorimport uz.hamroh.ui.theme.HamrohThemeimport javax.inject.Inject@AndroidEntryPointclass MainActivity : BaseActivity() {    @Inject    override lateinit var navigatorHolder: NavigatorHolder    @Inject lateinit var appCoordinator: AppCoordinator    override val navigator: Navigator = object : CustomAppNavigator(this, R.id.fragment_container_view) {}    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        appCoordinator.newRootScreen(AuthScreens.AuthSelection())        setContent {            Surface {                HamrohTheme {                    MainScreen()                }            }        }    }    @Composable    fun MainScreen() {        AndroidView(factory = { context ->            LayoutInflater.from(context).inflate(R.layout.main_activity, null)        })    }}