package uz.hamroh.mainimport android.annotation.SuppressLintimport android.os.Bundleimport android.view.LayoutInflaterimport androidx.activity.compose.setContentimport androidx.activity.viewModelsimport androidx.compose.foundation.layout.Boximport androidx.compose.foundation.layout.safeDrawingPaddingimport androidx.compose.material.Scaffoldimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.compose.ui.viewinterop.AndroidViewimport androidx.core.view.WindowCompatimport androidx.lifecycle.lifecycleScopeimport com.github.terrakok.cicerone.Navigatorimport com.github.terrakok.cicerone.NavigatorHolderimport dagger.hilt.android.AndroidEntryPointimport uz.hamroh.Rimport uz.hamroh.coroutines.launchMainimport uz.hamroh.ui.base.BaseActivityimport uz.hamroh.ui.base.CustomAppNavigatorimport uz.hamroh.ui.theme.HamrohThemeimport javax.inject.Inject@AndroidEntryPointclass MainActivity : BaseActivity() {    private val viewModel by viewModels<MainViewModel>()    override val navigatorHolder: NavigatorHolder by lazy { viewModel.getNavigationHolder() }    override val navigator: Navigator = object : CustomAppNavigator(this, R.id.fragment_container_view) {}    @SuppressLint("UnusedMaterialScaffoldPaddingParameter")    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        WindowCompat.setDecorFitsSystemWindows(window, false)        setContent {            HamrohTheme {                Scaffold(                    bottomBar = {}                ) {                    MainScreen()                }            }            if (savedInstanceState == null) {                viewModel.getCurrentNavigationState()            }        }    }    @Composable    fun MainScreen() {        Box(Modifier.safeDrawingPadding()){            AndroidView(factory = { context ->                LayoutInflater.from(context).inflate(R.layout.main_activity, null)            })        }    }}